import React, { useEffect, useState, useMemo } from "react";
import ReplyForm from "./ReplyForm";
import styles from "../styles/Comment.module.css";
import Pagination from "../Pagination/Pagination";

let PageSize = 6;

const CommentsList = () => {
  const [comments, setComments] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [openReply, setOpenReply] = useState(false);
  const [comId, setComId] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/comments")
      .then((response) => response.json())
      .then((data) => setComments(data))
      .catch((error) => console.error(error));
  }, []);

  const currentTableData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return comments.slice(firstPageIndex, lastPageIndex);
  }, [currentPage, comments]);

  const handleOnClickOpenReply = () => {
    if (openReply === false) {
      setOpenReply(true);
    } else setOpenReply(false);
  };

  return (
    <section className={styles.feedback}>
      <div className={styles.feedback__container}>
        <Pagination
          className={styles.pagination_bar}
          currentPage={currentPage}
          totalCount={comments.length}
          pageSize={PageSize}
          onPageChange={(page) => setCurrentPage(page)}
        />
        <h2 className={styles.feedback__title}>Відгуки</h2>
        {currentTableData.map((comment) => (
          <div key={comment._id}>
            <div className={styles.feedback__name_rate_date}>
              <div className={styles.feedback__name_rate}>
                <p>{comment.author}</p>
                <div
                  className={
                    comment.starRate === "1"
                      ? styles.rating_stars_first
                      : comment.starRate === "2"
                      ? styles.rating_stars_second
                      : comment.starRate === "3"
                      ? styles.rating_stars_third
                      : comment.starRate === "4"
                      ? styles.rating_stars_fourth
                      : comment.starRate === "5"
                      ? styles.rating_stars_fifth
                      : styles.rating_stars
                  }
                >
                  <span></span>
                  <span></span>
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
              <span className={styles.feedback__date}>{comment.day}</span>
            </div>
            <p>{comment.content}</p>
            <div
              className={styles.reply}
              onClick={() => {
                handleOnClickOpenReply();
                setComId(comment._id);
              }}
            >
              <span>
                <span className={styles.arrow__icon}>
                  <i class="fa-solid fa-reply fa-rotate-180"></i>
                </span>
                Відповісти
              </span>
            </div>
            {comment.replies?.map((message) => (
              <>
                <p>AVTOR: {message.author}</p>
                <p>COMMENT: {message.content}</p>
              </>
            ))}
            {
              <div
                className={openReply ? styles.reply__content : styles.unActive}
              >
                <ReplyForm commentId={comment._id} setComments={setComments} />
              </div>
            }
          </div>
        ))}
      </div>
    </section>
  );
};

export default CommentsList;
