  const [href, setHref] = useState('http://localhost:3000/')
  const location = useLocation();
  let paths = location.pathname.split("/").filter((path) => path !== "");

  useEffect(() => {
    let windowHref = window.location.href
    setHref(windowHref)
  },[])

  return (
   <>
