import React from "react";
import styles from "../styles/Main.module.css";
import ServiceComponent from "./ServiceComponent";

const ServicesComponent = ({ serviceText, service }) => {
  return (
    <>
      <div className={styles.service__info}>
        {serviceText.map((info) => (
          <>
            <h2 className={styles.service__title}>{info.title}</h2>
            <p className={styles.service__text}>
              {info.txt1.split("\n").map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
              ))}
            </p>
          </>
        ))}

        <ServiceComponent service={service} />
      </div>
    </>
  );
};

export default ServicesComponent;
