import React from "react";
import styles from "../styles/Main.module.css";

const ServiceComponent = ({ service }) => {


  return (
    <>
      <section className={styles.service__block}>
        <div className={styles.service__content}>
          <div className={styles.hodovaya__banner}>
            <h1 className={styles.service__banner_title}>
            </h1>
          </div>
          <div className={styles.service__info}>
            <h2 className={styles.service__title}></h2>
            <p className={styles.service__text}></p>
            <ul className={styles.service_table}>
              <li className={styles.service_table__first}>
                <span>Послуги</span>
                <span>Ціни</span>
              </li>
              {service.map((service_item) => {
                let newText =
                  service_item.warning.charAt(0).toUpperCase() +
                  service_item.warning.slice(1);

                return (
                  <>
                    <li>
                      <span>{service_item.name}</span>
                      <span>{service_item.price}</span>
                    </li>
                    <li
                      className={
                        service_item.warning.length !== 0
                          ? styles.under_service__text
                          : styles.unActive
                      }
                    >
                      <div className={styles.warning}>
                        <i class="fa-solid fa-arrow-up"></i>
                        <p>{newText}</p>
                        <i class="fa-solid fa-arrow-up"></i>
                      </div>
                    </li>
                  </>
                );
              })}
            </ul>
          </div>
        </div>
      </section>
    </>
  );
};

export default ServiceComponent;
