import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/Navbar.module.css";

const LinksBar = () => {
  const [openMenu, setOpenMenu] = useState(false);

  const handleOpenMenu = () => {
    if (openMenu === false) {
      setOpenMenu(true);
    } else {
      setOpenMenu(false);
    }
  };

  return (
    <nav>
      <div className={styles.nav__content}>
        <Link to="/">Головна</Link>
        <Link className={styles.nav__menu} onClick={handleOpenMenu}>
          Послуги<span className={styles.arrow_down_icon}></span>
          {openMenu ? <ul className={styles.active__menu}>
            <Link><li>Ремонт двигунів</li></Link>
            <Link><li>Ремонт ходової</li></Link>
            <Link><li>ТО автомобіля</li></Link>
            <Link><li>Автоелектрик</li></Link>
            <Link><li>Автокондиціонер</li></Link>
            <Link><li>Автокондиціонер США</li></Link>
            <Link><li>Розвал-схождения 3D</li></Link>
         </ul> : null}
        </Link>
        <Link>Відгуки</Link>
        <Link>Про нас</Link>
        <Link>Контакти</Link>
        <Link>Блог</Link>
      </div>

     
    </nav>
  );
};

export default LinksBar;
