import React, { useEffect, useState } from 'react';
import ReplyForm from './ReplyForm';

const CommentsList = () => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/comments')
      .then(response => response.json())
      .then(data => setComments(data))
      .catch(error => console.error(error));
  }, []);

 console.log(comments)

  return (
    <div>
      <h2>Список комментариев</h2>
      {comments.map(comment => (
        <div key={comment._id}>
          <p>Автор: {comment?.author}</p>
          <p>Содержание: {comment.content}</p>
          <p>Comment ID: {comment._id}</p> {/* Добавьте эту строку для вывода commentId */}
          {comment.replies?.map((message) => (
            <>
            <p>AVTOR: {message.author}</p>
            <p>COMMENT: {message.content}</p>
            </>
          ))}
          <ReplyForm commentId={comment._id} setComments={setComments} />
        </div>
      ))}
    </div>
  );
}

export default CommentsList;
