import React, { useState, useEffect } from "react";
import CommentsList from "./CommentsList";
import styles from "../styles/Comment.module.css";

const CommentForm = () => {
  const [author, setAuthor] = useState("");
  const [content, setContent] = useState("");
  const [day, setDay] = useState("");
  const [starRate, setStarRate] = useState(0);

  useEffect(() => {
    const date = new Date();
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    let formattedDate;

    if (date.getMonth() + 1 < 10) {
      formattedDate = `${day}.0${month}.${year}`;
    } else if (date.getDate() < 10) {
      formattedDate = `0${day}.${month}.${year}`;
    } else {
      formattedDate = `${day}.${month}.${year}`;
    }

    setDay(formattedDate);
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();

    fetch("http://localhost:5000/comments", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ author, content, day, starRate }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message); // Вывод сообщения об успешном создании комментария
        setAuthor("");
        setContent("");
      })
      .catch((error) => console.error(error));
  };

  return (
    <div>
      <CommentsList />
      <h2>Оставить комментарий</h2>
      <form onSubmit={handleSubmit}>
        <div className={styles.container}>
          <div className={styles.rating}>
            <input
              type="radio"
              name="clr1"
              onChange={() => setStarRate(5)}
              style={{ "--c": "#ff9933" }}
            />
            <input
              type="radio"
              name="clr1"
              onChange={() => setStarRate(4)}
              style={{ "--c": "#ff9933" }}
            />
            <input
              type="radio"
              name="clr1"
              onChange={() => setStarRate(3)}
              style={{ "--c": "#ff9933" }}
            />
            <input
              type="radio"
              name="clr1"
              onChange={() => setStarRate(2)}
              style={{ "--c": "#ff9933" }}
            />
            <input
              type="radio"
              name="clr1"
              onChange={() => setStarRate(1)}
              style={{ "--c": "#ff9933" }}
            />
          </div>
        </div>
        <div>
          <label>Автор:</label>
          <input
            type="text"
            value={author}
            onChange={(event) => setAuthor(event.target.value)}
          />
        </div>
        <div>
          <label>Содержание:</label>
          <textarea
            value={content}
            onChange={(event) => setContent(event.target.value)}
          ></textarea>
        </div>
        <button type="submit">Отправить</button>
      </form>

    </div>
  );
};

export default CommentForm;
