import React, { useEffect, useState } from "react";
import ReplyForm from "./ReplyForm";
import styles from "../styles/Comment.module.css";

const CommentsList = () => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/comments")
      .then((response) => response.json())
      .then((data) => setComments(data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <section className={styles.feedback}>
      <div className={styles.feedback__container}>
        <h2 className={styles.feedback__title}>Відгуки</h2>
        {comments.map((comment) => (
          <div key={comment._id}>
            <p>Автор: {comment.author}</p>
            <div
              className={
                comment.starRate === "1"
                  ? styles.rating_stars_first
                  : comment.starRate === "2"
                  ? styles.rating_stars_second
                  : comment.starRate === "3"
                  ? styles.rating_stars_third
                  : comment.starRate === "4"
                  ? styles.rating_stars_fourth
                  : comment.starRate === "5"
                  ? styles.rating_stars_fifth
                  : styles.rating_stars
              }
            >
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
            </div>
            <div className={styles.feedback__name_rate_date}>
              <div className={styles.feedback__name_rate}>
              <span className={styles.feedback__name}>{comment.author}</span>
              <span className={styles.feedback__rate}>{comment.starRate}</span>
              </div>
              <span className={styles.feedback__date}>{comment.day}</span>
            </div>
            <p>Содержание: {comment.content}</p>
            {comment.replies?.map((message) => (
              <>
                <p>AVTOR: {message.author}</p>
                <p>COMMENT: {message.content}</p>
              </>
            ))}
            <ReplyForm commentId={comment._id} setComments={setComments} />
          </div>
        ))}
      </div>
    </section>
  );
};

export default CommentsList;
