import React from "react";
import news1 from "../img/news1.jpeg";
import news2 from "../img/news2.jpeg";
import whatWill1 from "../img/whatWill.jpeg";
import autoServices1 from "../img/autoServices.jpeg";
import advices1 from "../img/advices.jpeg";
import styles from "../styles/Blog.module.css";
import { Link } from "react-router-dom";
import { useState } from "react";

const Blog = ({ setSingleBlog }) => {
  const [blog, setBlog] = useState("Всі новини");

  const news = [
    {
      id: 1,
      year: "12.10.2020",
      image: news1,
      title: "Новини",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/news",
      title_info: "В Украине представили новые номера для элетроавтомобилей",
      info: "Новые номерные знаки имеют зеленый цвет и предназначены для собственников электротранспорта. На Львовщине были...",
    },
    {
      id: 2,
      year: "25.09.2020",
      image: news2,
      title: "Новини",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/news",
      title_info:
        "Посмертное донорство и группа крови, в Украине вводят новые...",
      info: "Новые бланки будут с отметками о согласии на посмертное донорство и группой крови...",
    },
  ];

  const whatWill = [
    {
      id: 3,
      year: "29.08.2020",
      image: whatWill1,
      title: "Что будет если...",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/whatwill",
      title_info: "Что будет если смешать минералку и синтетику",
      info: "Еще со времен появления синтетического масла 'синтетики' на автомобильном рынке, некоторые люди задаются...",
    },
  ];

  const autoService = [
    {
      id: 4,
      year: "18.07.2020",
      image: autoServices1,
      title: "Обслуживание автомобиля",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/autoservices",
      title_info: "Промывка двигателя: нужна или нет?",
      info: "Когда вы заходите на множество форумов с вопросом, промывать двигатель или нет – то люди делятся на два лагеря, те кто з...",
    },
  ];
  const advices = [
    {
      id: 5,
      year: "17.07.2020",
      image: advices1,
      title: "Советы автомобилистам",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/advices",
      title_info: "Как правильно подобрать аккумулятор?",
      info: "Во-первых: аккумулятор должен иметь хороший и целый корпус без трещин, сколов, и т.д., на нем должны быть...",
    },
  ];

  const allNews = [
    {
      id: 1,
      year: "12.10.2020",
      image: news1,
      title: "Новини",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/news",
      title_info: "В Украине представили новые номера для элетроавтомобилей",
      info: "Новые номерные знаки имеют зеленый цвет и предназначены для собственников электротранспорта. На Львовщине были...",
    },
    {
      id: 2,
      year: "25.09.2020",
      image: news2,
      title: "Новини",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/news",
      title_info:
        "Посмертное донорство и группа крови, в Украине вводят новые...",
      info: "Новые бланки будут с отметками о согласии на посмертное донорство и группой крови...",
    },
    {
      id: 3,
      year: "29.08.2020",
      image: whatWill1,
      title: "Что будет если...",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/whatwill",
      title_info: "Что будет если смешать минералку и синтетику",
      info: "Еще со времен появления синтетического масла 'синтетики' на автомобильном рынке, некоторые люди задаются...",
    },
    {
      id: 4,
      year: "18.07.2020",
      image: autoServices1,
      title: "Обслуживание автомобиля",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/autoservices",
      title_info: "Промывка двигателя: нужна или нет?",
      info: "Когда вы заходите на множество форумов с вопросом, промывать двигатель или нет – то люди делятся на два лагеря, те кто з...",
    },
    {
      id: 5,
      year: "17.07.2020",
      image: advices1,
      title: "Советы автомобилистам",
      link: "http://localhost:3000/blog/news",
      title_link: "http://localhost:3000/blog/advices",
      title_info: "Как правильно подобрать аккумулятор?",
      info: "Во-первых: аккумулятор должен иметь хороший и целый корпус без трещин, сколов, и т.д., на нем должны быть...",
    },
  ];

  let blogChanger;

  if (blog === "НОВОСТИ") {
    blogChanger = news;
  } else if (blog === "ЧТО БУДЕТ ЕСЛИ...") {
    blogChanger = whatWill;
  } else if (blog === "ОБСЛУЖИВАНИЕ АВТОМОБИЛЯ") {
    blogChanger = autoService;
  } else if (blog === "СОВЕТЫ АВТОМОБИЛИСТАМ") {
    blogChanger = advices;
  } else if (blog === "Всі новини") {
    blogChanger = allNews;
  } else {
    blogChanger = allNews;
  }

  return (
    <section className={styles.blog}>
      <div className={styles.blog__content}>
        <h1 className={styles.blog__title}>Блог</h1>
        <div className={styles.blog__content_sides}>
          <div className={styles.blog__left_side}>
            {blogChanger.map((blog) => (
              <div className={styles.blog__block}>
                <Link
                 onClick={() => setSingleBlog(blog.id)}
                 to={blog.link}
                 className={styles.image__link}
                >
                  <div className={styles.blog__image_block}>
                    <h1 className={styles.read_more_img}>Читати більше</h1>
                    <img
                      className={styles.blog__img}
                      src={blog.image}
                      alt="blog_image"
                    />
                  </div>
                </Link>
                <div className={styles.blog__info_content}>
                  <span className={styles.blog__year}>
                    <span className={styles.shedule_icon}></span>
                    {blog.year}
                  </span>
                  <Link
                    to={blog.title_link}
                    className={styles.blog__block_title}
                  >
                    # {blog.title}
                  </Link>
                  <h3 className={styles.blog__title_info}>{blog.title_info}</h3>
                  <p className={styles.blog__info}>{blog.info}</p>
                </div>
                <Link
                  to={blog.link}
                  onClick={(e) => setSingleBlog(e.target.href)}
                  className={styles.blog__read_more}
                >
                  Читати більше
                </Link>
              </div>
            ))}
          </div>
          <div className={styles.blog__right_side}>
            <div className={styles.categories_block}>
              <h1 className={styles.blog__categories_title}>Категорії</h1>
              <ul className={styles.blog__category_list}>
                <Link to="/blog" onClick={(e) => setBlog(e.target.innerText)}>
                  <li>Всі новини</li>
                </Link>
                <li>
                  <span onClick={(e) => setBlog(e.target.innerText)}>
                    СОВЕТЫ АВТОМОБИЛИСТАМ
                  </span>
                  <span className={styles.blog__length}>
                    ({advices.length})
                  </span>
                </li>
                <li>
                  <span onClick={(e) => setBlog(e.target.innerText)}>
                    ОБСЛУЖИВАНИЕ АВТОМОБИЛЯ
                  </span>
                  <span className={styles.blog__length}>
                    ({autoService.length})
                  </span>
                </li>
                <li>
                  <span onClick={(e) => setBlog(e.target.innerText)}>
                    ЧТО БУДЕТ ЕСЛИ...
                  </span>
                  <span className={styles.blog__length}>
                    ({whatWill.length})
                  </span>
                </li>
                <li>
                  <span onClick={(e) => setBlog(e.target.innerText)}>
                    НОВОСТИ
                  </span>
                  <span className={styles.blog__length}>({news.length})</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Blog;
