import React, { useState } from "react";
import styles from "../styles/Main.module.css";

const Collapsible = ({ title, content, index }) => {
  const [isActive, setIsActive] = useState(false);

  const toggleCollapsible = () => {
    setIsActive(!isActive);
    // Тут ви можете використовувати індекс
    console.log('Clicked on collapsible with index:', index);
  };

  return (
    <div className={`${styles.accourdion__item} ${isActive ? styles.active : ''}`}>
      <div className={styles.accordion__title} onClick={toggleCollapsible}>
        <h2>{title}</h2>
      </div>
      <div className={styles.accordion_content}>{content}</div>
    </div>
  );
};

export default Collapsible;
